import {createMuiTheme} from '@material-ui/core/styles';
import teal from '@material-ui/core/colors/teal';

const theme = createMuiTheme({
    palette: {
        primary: {
            light: teal[50],
            main: teal[500],
            dark: teal[900],
            contrastText: teal[50],
        }
    },
    typography: {
        fontFamily: [
            '-apple-system',
            'BlinkMacSystemFont',
            '"Segoe UI"',
            'Roboto',
            '"Helvetica Neue"',
            'Arial',
            'sans-serif',
            '"Apple Color Emoji"',
            '"Segoe UI Emoji"',
            '"Segoe UI Symbol"',
          ].join(','),
        text: {
          fontSize: 12,
        },
        body1: {
          fontWeight: 500,
        },
        button: {
            fontFamily: 'Arial',
            fontStyle: 'bold',
        },
      },
})

export default theme;
